import React from "react";
import "./Overview.css";
import { Row, Col } from "react-bootstrap";
import WebIcon from "../../Assets/web-icon.png";
import BackEndIcon from "../../Assets/procedure-icon.png";
import FrontEndIcon from "../../Assets/frontend-icon.png";
import ChessIcon from "../../Assets/chess-icon2.png";
import { ASPECTS, ASPECT_DETAILS } from "../../data/data";

export default function Overview() {
    const aspectIcons = [BackEndIcon, FrontEndIcon, WebIcon, ChessIcon];
    const backendTools = "Java/C++/Kotlin/Go";
    const frontendTools = "ReactJS/Bootstrap";
    const contentCreator = "4k+ Subscribers";
    const chess = "Chess is the BEST!";
    const aspectDetails = [backendTools, frontendTools, contentCreator, chess];

    return <div className="overview">
        <Row>
            <Col md={1} ></Col>
            <Col>
                <h1>OVERVIEW</h1>
            </Col>
        </Row>
        <Row>
            <Col md={1} ></Col>
            <Col md={7}>
            <p className="overview-description">I'm a software engineer with experience in both Backend and Frontend. My passion lies in leveraging technology to tackle complex business challenges. Fun facts, I'm a technical content creator and chess player in my free time!</p>
            </Col>
        </Row>
        <Row style={{ marginTop: '30px' }}>
            <Col md={1}></Col>
            {aspects.map((aspect, index) => (
                <Col md={2} key={index} className="aspect">
                    <div className="aspect-icon">
                        <img src={aspectIcons[index]}/>
                    </div>
                    <h4>{aspect}</h4>
                    <p>{aspectDetails[index]}</p>
                </Col>
            ))}

        </Row>
    </div>
}

// I'm a skilled Full Stack software developer with experience in TypeScript and JavaScript, and expertise in frameworks like React, Node.js, MongoDB and Express.js. 
// I'm a quick learner and collaborate closely with clients to create efficient, scalable, and user-friendly 
// solutions that solve real-world problems. Let's work together to bring your ideas to life!
